<?php

namespace App\Repository;

use Doctrine\Bundle\DoctrineBundle\Repository\ServiceEntityRepository;
use Doctrine\Common\Persistence\ManagerRegistry;
use Doctrine\ORM\NonUniqueResultException;
use Doctrine\ORM\QueryBuilder;
use App\Entity\Manufacturer;

/**
 * ManufacturerRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ManufacturerRepository extends ServiceEntityRepository
{
    /**
     * Repository constructor.
     * @param ManagerRegistry $registry
     */
    public function __construct(ManagerRegistry $registry)
    {
        parent::__construct($registry, Manufacturer::class);
    }

    /**
     * @param bool $showEmpty
     * @param string $order
     * @param string $sort
     * @return Manufacturer[]|null
     */
    public function getAllManufacturers(bool $showEmpty = true, string $sort = 'name', string $order = 'ASC'): array
    {
        $qb = $this->getEntityManager()
            ->createQueryBuilder()
            ->select('c')
            ->from(Manufacturer::class, 'c');

        if (!$showEmpty) {
            $qb->innerJoin('c.products', 'p')
                ->andWhere('p.quantity <> 0');
        }

        $qb->orderBy('c.' . $sort, $order);

        return $qb->getQuery()->getResult();
    }

    /**
     * query for admin paginator
     *
     * @return QueryBuilder
     */
    public function getAllManufacturersAdminQB(): QueryBuilder
    {
        $qb = $this->getEntityManager()
            ->createQueryBuilder()
            ->select('m')
            ->from(Manufacturer::class, 'm');

        return $qb;
    }

    /**
     * @param string $slug
     * @return Manufacturer|null
     * @throws NonUniqueResultException
     */
    public function findBySlug(string $slug): ?Manufacturer
    {
        return $this->getEntityManager()
            ->createQueryBuilder()
            ->select('m')
            ->from(Manufacturer::class, 'm')
            ->where('m.slug = :slug')
            ->setParameter('slug', $slug)
            ->getQuery()
            ->getOneOrNullResult();
    }

    /**
     * @return Manufacturer|null
     * @throws NonUniqueResultException
     */
    public function getFirstManufacturer(): ?Manufacturer
    {
        return $this->getEntityManager()
            ->createQueryBuilder()
            ->select('ma')
            ->from(Manufacturer::class, 'ma')
            ->orderBy('ma.id', 'ASC')
            ->setMaxResults(1)
            ->getQuery()
            ->getOneOrNullResult();
    }

    /**
     * @return array
     */
    public function getArrayForSitemap(): array
    {
        return $this->createQueryBuilder('m')
            ->select('m.slug, m.dateUpdated')
            ->getQuery()
            ->getArrayResult();
    }
}
